  }

  loadPage = async () => {
    // get page of items from api
    const params = new URLSearchParams(window.location.search);
    console.log(
      "ðŸš€ ~ file: userContainer.js ~ line 324 ~ UserContainer ~ loadPage ~ params",
      params
    );
    const page = parseInt(params.get("page")) || 1;
    console.log(
      "ðŸš€ ~ file: userContainer.js ~ line 329 ~ UserContainer ~ loadPage ~ page",
      page
