import React, { Component } from "react";
import { connect } from "react-redux";
import { deleteUsers, fetchAllUsers, fetchSearchedUser } from "../redux";
// import UserContainerForm from "./UserContainerForm";
// import Autocomplete from "@material-ui/lab/Autocomplete";
// import childComponent from "./UserContainerUpdate";
// import UserContainerUpdate from "./UserContainerUpdate";
// import SearchField from "react-search-field";
// // import SearchBar from "./SearchBar";
// // import SearchPage from "./searchPage";
// import axios from "axios";
import { BrowserRouter as Router, Link, Redirect } from "react-router-dom";
import ReactPaginate from "react-paginate";
import axios from "axios";
class UserContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      users: [],
      offset: 1,
      data: [],
      perPage: 5,
      pageCount: 0,
      isLoading: true,
      searchTerm: "",
      pager: {},
    };
  }
  componentDidMount() {
    this.loadPage();
  }
  componentWillMount() {
    this.loadPage();
  }
  componentDidUpdate(prevProps, prevState, snapShot) {
    if (
      prevProps.userData !== this.props.userData ||
      prevProps.totalPage !== this.props.totalPage
    ) {
      this.setState({
        users: this.props.userData,
        pageCount: this.props.totalPage,
      });
    }
    // this.loadPage();
  }

  loadPage = () => {
    // get page of items from api
    const { offset, perPage } = this.state;

    const { userData, totalPage, fetchAllUsers } = this.props;

    const params = new URLSearchParams(window.location.search);

    const pagep = parseInt(params.get("page")) || 1;

    // if (pagep !== this.state.pager.currentPage) {
    fetchAllUsers(offset, perPage);
    this.setState({ users: this.props.userData, pageCount: totalPage });

    // }
  };
  searchPage = (searchTerm, selectedPage) => {
    this.props.fetchSearchedUser(searchTerm, selectedPage);

    this.setState({
      users: this.props.userData,
      pageCount: this.props.totalPage,
    });
  };
  handlePageClick = async (e) => {
    const selectedPage = e.selected;

    const { searchTerm, perPage } = this.state;
    if (searchTerm === "") {
      this.props.fetchAllUsers(selectedPage, perPage);
      this.setState({ offset: selectedPage + 1 }, () => {
        this.loadPage();
      });
    } else {
      this.props.fetchSearchedUser(searchTerm, selectedPage);

      this.setState({ offset: selectedPage + 1 }, () => {
        this.searchPage(searchTerm, this.state.offset);
      });
    }
    Offset(selectedPage + 1);
  };
  addUser = async () => {
    this.props.history.push("/adduser");
    // this.refreshPage();
    // <UserContainerForm />;
  };
  deleteUserData = async (id, name) => {
    const { offset, perPage } = this.state;
    if (window.confirm(`Are you sure you want to Delete ${name}?`)) {
      await deleteUsers(id);
    } else {
      this.props.history.push("/home");
    }
    this.props.fetchAllUsers(offset, perPage);
    // this.refreshPage();
  };
  editUserData = async (id) => {
    this.props.history.push(`/updateuser/${id}`);
    // this.refreshPage();
  };
  refreshPage = () => {
    window.location.reload();
  };

  filterContent = (users, searchTerm) => {
    if (searchTerm === "") {
      // this.refreshPage();
      this.loadPage();
      // this.getData();
    } else {
      const result = users.filter((user) =>
        user.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
      this.setState({ users: result });
      // console.log(
      //   "ðŸš€ ~ file: userContainer.js ~ line 428 ~ UserContainer ~ users",
      //   this.state.users,
      //   this.props
      // );
      // this.getData();
    }
  };
  onValueChange = (e) => {
    const searchTerm = e.currentTarget.value;
    this.setState({ searchTerm: searchTerm });

    if (searchTerm === "") {
      // this.refreshPage();
      this.loadPage();
      // this.getData();
    } else {
      this.searchPage(searchTerm, this.state.offset);
    }
  };
  render() {
    const { users } = this.state;
    return (
      <div className="container App">
        <Router>
          <h2>Users List</h2>
          <div className="container text-right">
            <Link to={{ pathname: `/adduser` }}>
              <button
                onClick={this.addUser}
                className="btn btn-primary btn-md m-1  "
              >
                ADD USER
              </button>
            </Link>
          </div>
          <div className="container mt-mb-10 text-left">
            <div className="w-100 mt-mb-10  justify-content-left ui icon input">
              {/* <SearchPage /> */}
              <span className="icon-input-btn">
                <i className="fa fa-search"></i>
                <input
                  // ref={inputEl}
                  type="search "
                  placeholder="Search Users"
                  className="mt-mb-7 form-control  "
                  // v
                  name="searchTerm"
                  onChange={this.onValueChange}
                />
              </span>
            </div>
          </div>
          <div className=" container  ">
            <table className="table mt-5 table-striped justify-content-center">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Age</th>
                  <th>Email</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody className="text-left">
                {users?.length > 0 &&
                  users?.map((user) => (
                    <tr key={user._id}>
                      <td>{user.name}</td>
                      <td>{user.age}</td>
                      <td>{user.email}</td>
                      <td>
                        <Link
                          to={{
                            pathname: `/updateuser/${user._id}`,
                          }}
                        >
                          <button
                            // onClick={<Redirect to="/updateuser/${user._id}" />}
                            onClick={() => this.editUserData(user._id)}
                            className="btn btn-success btn-sm m-1  "
                            // component={Link}
                            // to={`/updateuser/${user._id}`}
                          >
                            UPDATE
                          </button>
                        </Link>
                        <button
                          onClick={() => {
                            this.deleteUserData(user._id, user.name);
                          }}
                          className="btn btn-danger btn-sm m-1 "
                        >
                          <i className="fa fa-trash"></i>
                          DELETE
                        </button>
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>

            <ReactPaginate
              previousLabel={"prev"}
              nextLabel={"next"}
              breakLabel={"..."}
              breakClassName={"break-me"}
              pageCount={this.state.pageCount}
              marginPagesDisplayed={2}
              pageRangeDisplayed={5}
              onPageChange={this.handlePageClick}
              containerClassName={"pagination"}
              subContainerClassName={"pages pagination"}
              activeClassName={"active"}
            />
          </div>
        </Router>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    fetchAllUsers: (page, limit) => dispatch(fetchAllUsers(page, limit)),
    fetchSearchedUser: (searchTerm, offset) =>
      dispatch(fetchSearchedUser(searchTerm, offset)),
  };
};
export default connect(
  (state) => ({
    userData: state.users,
    loadingData: state.loading,
    totalPage: state.page,
  }),
  mapDispatchToProps
)(UserContainer);
