import React, { Component, useState, useEffect } from "react";
import { connect } from "react-redux";
import { fetchUser, updateUsers } from "../redux";
// import React from 'react'
import { useParams, useHistory, withRouter } from "react-router-dom";
import HeaderUser from "./HeaderUser";
// const location = useLocation();
const initialState = {
  name: "",
  age: "",
  email: "",
};
function UserContainerUpdateHook({ userData, fetchUser }, props) {
  const [user, setUser] = useState(initialState);
  const [users, setUsers] = useState([]);
  const { name, age, email } = user;
  const { id } = useParams();
  let history = useHistory();
  console.log(
    "params DAta",
    useParams(),
    userData,
    userData.name,
    userData.age,
    userData.email
  );
  useEffect(() => {
    fetchUser(id);
    setUsers(userData);
    // console.log("user in useeffect", users);
  }, []);

  console.log("params DAta", useParams(), users, userData.age, userData.email);
  const onValueChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
    console.log("user==>", user.name, user.age, user.email);
  };

  console.log("out side update state", id, name, age, email);

  const editUserDetails = (e) => {
    console.log("in else part", user);
    console.log("users", users);
    if (!user.name || !user.age || !user.email) {
      if (!user.name) {
        // const uname = users.name;
        user.name = userData[0].name;
        console.log("user name", user.name);
      }
      if (!user.age) {
        // age = userData.map((user) => user.age);
        user.age = userData[0].age;
        console.log("user age", user);
      }
      if (!email) {
        // email = userData.map((user) => user.email);
        user.email = userData[0].email;
        console.log("user in if ", user);
        history.push("/home");
        updateUsers(id, user);
      }
    } else {
      console.log("in else part", user);
      history.push("/home");
      updateUsers(id, user);
    }
  };
  return (
    <div className="container ">
      <HeaderUser />
      {userData.map((getuser) => {
        return (
          <div key={getuser._id}>
            <form onSubmit={editUserDetails()}>
              <div className="form-group text-left">
                <label>Name:</label>
                <input
                  type="text"
                  name="name"
                  className="form-control"
                  defaultValue={getuser.name}
                  // value={user.name}
                  onChange={onValueChange}
                />
              </div>
              <div className="form-group text-left">
                <label>Age:</label>
                <input
                  type="text"
                  name="age"
                  className="form-control"
                  defaultValue={getuser.age}
                  // value={user.age}
                  onChange={onValueChange}
                />
              </div>
              <div className="form-group text-left">
                <label>Email:</label>
                <input
                  type="email"
                  name="email"
                  className="form-control"
                  defaultValue={getuser.email}
                  onChange={onValueChange}
                />
              </div>
              <div className="form-group text-left">
                <button
                  className="btn btn-primary"
                  // onClick={() => editUserDetails()}
                  type="submit"
                >
                  Update
                </button>
              </div>
            </form>
          </div>
        );
      })}
    </div>
  );
}

const mapStateToProps = (state) => {
  console.log("state", state);
  return {
    userData: state.users,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    fetchUser: (id) => dispatch(fetchUser(id)),
    updateUsers: (id, user) => dispatch(updateUsers(id, user)),
  };
};
export default connect(
  (state) => (
    console.log(
      "%c üç©: state ",
      "font-size:16px;background-color:#9b6db7;color:white;",
      state
    ),
    { userData: state.users, loadingData: state.loading }
  ),
  mapDispatchToProps
)(UserContainerUpdateHook);
