import React, { Component, useState, useEffect } from "react";
import { connect } from "react-redux";
import { fetchUser, updateUsers } from "../redux";
// import React from 'react'
import { useLocation, useParams, Redirect } from "react-router-dom";
// const location = useLocation();
const initialState = {
  name: "",
  age: "",
};
function UserContainerUpdate({ userData, fetchUser }, props) {
  const [user, setUser] = useState(initialState);
  const { name, age } = user;
  const { id } = useParams();
  console.log(
    "params DAta",
    useParams(),
    userData,
    userData.name,
    userData.age
  );
  useEffect(() => {
    fetchUser(id);
  }, []);

  // fetchUser = async () => {
  //   // const response = await getUsers(id);
  //   setUser(userData);
  // };

  // const history = useHistory();
  console.log("params DAta", useParams(), userData.name, userData.age);
  const onValueChange = (e) => {
    console.log(e.target.value);
    setUser({ ...user, [e.target.name]: e.target.value });
  };
  const onSubmitData = (e) => {
    // e.preventDefault();
    updateUsers(id, name, age);
    console.log("update state", id, name, age);
  };
  console.log("update state", id, name, age);
  return (
    <div className="container ">
      {userData.map((user) => (
        <p key={user._id}>
          <form onSubmit={onSubmitData}>
            <div className="form-group text-left">
              <label>Name:</label>
              <input
                type="text"
                name="name"
                className="form-control"
                defaultValue={user.name}
                value={name}
                onChange={(e) => {
                  console.log(e.target.value);
                  setUser({ name: e.target.value });
                }}
              />
            </div>
            <div className="form-group text-left">
              <label>Age:</label>
              <input
                type="text"
                name="age"
                className="form-control"
                value={age}
                onChange={
                  ((e) => {
                    console.log(e.target.value);
                    setUser({ age: e.target.value });
                  }) && <Redirect to="/home" />
                }
              />
            </div>
            <button
              className="btn btn-primary"
              // onClick={() => setModalIsOpen(false)}
              type="submit"
            >
              Update
            </button>
          </form>
        </p>
      ))}
    </div>
  );
}
