import React, { Component, useEffect } from "react";
import { connect } from "react-redux";
import { fetchUsers } from "../redux";
import UserContainerForm from "./UserContainerForm";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams,
  useHistory,
} from "react-router-dom";
// import UserContainerUpdate from "./UserContainerUpdate";
import childComponent from "./UserContainerUpdate";
import UserContainerUpdate from "./UserContainerUpdate";
const history = useHistory();
function UserContainer({ userData, fetchUsers }, props) {
  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <div>
      <Router>
        <h2>Users List</h2>
        <div className="container text-right">
          <Link to={{ pathname: `/adduser` }}>
            <button
              onClick={<UserContainerForm />}
              className="btn btn-primary btn-md m-1  "
            >
              ADD USER
            </button>
          </Link>
        </div>
        <div className=" container ">
          <table className="table  table-striped justify-content-center">
            <thead>
              <tr>
                <th>Name</th>
                <th>Age</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody className="text-left">
              {userData.map((user) => (
                <tr key={user._id}>
                  <td>{user.name}</td>
                  <td>{user.age}</td>
                  <td>
                    <Link
                      to={{
                        pathname: `/updateuser`,
                        // state: { name: user.name, age: user.age },
                      }}
                    >
                      <button
                        onClick={
                          <UserContainerUpdate
                            name={user.name}
                            age={user.age}
                          />
                        }
                        className="btn btn-success btn-sm m-1  "
                      >
                        UPDATE
                      </button>
                    </Link>
                    <button className="btn btn-danger btn-sm m-1 ">
                      DELETE
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </Router>
    </div>
  );
}

