import React, { Component, useEffect, useState, useRef } from "react";
import { connect } from "react-redux";
import { deleteUsers, fetchAllUsers, fetchUsers, updateUsers } from "../redux";
// import Autocomplete from "@material-ui/lab/Autocomplete";
// import childComponent from "./UserContainerUpdate";
// import SearchField from "react-search-field";
// // import SearchBar from "./SearchBar";
// // import SearchPage from "./searchPage";
// import axios from "axios";
import {
  BrowserRouter as Router,
  Link,
  Redirect,
  useHistory,
} from "react-router-dom";
import ReactPaginate from "react-paginate";
const UserContainerHook = ({ fetchUsers, userData, loadingData }, props) => {
  const [users, setUsers] = useState(userData);
  const [offset, setOffset] = useState(1);
  const [data, setData] = useState([]);
  const [perPage] = useState(5);
  const [pageCount, setPageCount] = useState(0);
  // const [user, setUser] = useState([]);
  const inputEl = useRef("");
  // const { name, age, email } = user;
  const history = useHistory();
  useEffect(() => {
    loadPage();
  }, []);
  const loadPage = () => {
    const { offset, perPage } = this.state;

    const { userData, totalPage, fetchAllUsers } = this.props;

    const params = new URLSearchParams(window.location.search);

    const pagep = parseInt(params.get("page")) || 1;

    fetchAllUsers(offset, perPage);
    this.setState({ users: this.props.userData, pageCount: totalPage });
  };
  searchPage = (searchTerm, selectedPage) => {
    this.props.fetchSearchedUser(searchTerm, selectedPage);

    this.setState({
      users: this.props.userData,
      pageCount: this.props.totalPage,
    });
  };

  useEffect(() => {
    //if (loadingData === false) {
    getData();
    //}
  }, [offset]);

  const deleteUserData = async (id, name) => {
    if (window.confirm(`Are you sure you want to Delete ${name}?`)) {
      await deleteUsers(id);
    } else {
      history.push("/home");
    }
    fetchAllUsers();
    getData();
  };
  const getData = async () => {
    const data = userData;
    console.log(
      "%c 🏑: getData -> userData ",
      "font-size:16px;background-color:#e9e1e4;color:black;",
      userData
    );
    console.log("set pagination here.....", data);
    // const newOffset = offset > 0 ? offset - 1 : 0;
    // console.log("newOffset :: ", newOffset);
    const indexOfLastTodo = offset * perPage;
    const indexOfFirstTodo = indexOfLastTodo - perPage;
    const slice = data.slice(indexOfFirstTodo, indexOfLastTodo);
    console.log(
      "file: userContainerHook.js ~ line 56 ~ getData ~ slice",
      slice
    );
    setUsers(slice);
    setPageCount(Math.ceil(data.length / perPage));
  };

  const handlePageClick = (e) => {
    const selectedPage = e.selected;
    setOffset(selectedPage + 1);
  };
  const addUser = async () => {
    history.push("/adduser");
    // <UserContainerHookForm />;
  };
  const filterContent = (users, searchTerm) => {
    const result = users.filter((user) =>
      user.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    console.log("result", result);
    setUsers(result);
  };
  const onValueChange = (e) => {
    console.log(e.currentTarget.value);
    const searchTerm = e.currentTarget.value;
    fetchAllUsers();
    // axios.get("http://localhost:9000/").then((response) => {
    console.log(
      "file: userContainerHook.js ~ line 82 ~ getData ~ offset",
      offset
    );
    if (userData) {
      console.log("users for search", userData);
      filterContent(userData, searchTerm);
    }
    // });
  };

  return (
    <div>
      <Router>
        <h2>Users List</h2>
        <div className="container text-right">
          <Link to={{ pathname: `/adduser` }}>
            <button onClick={addUser} className="btn btn-primary btn-md m-1  ">
              ADD USER
            </button>
          </Link>
        </div>
        <div className="container mt-mb-10 text-left">
          <div className="w-100 mt-mb-10  justify-content-left ui icon input">
            {/* <SearchPage /> */}
            <input
              // ref={inputEl}
              type="search "
              placeholder="Search Users"
              className="mt-mb-7 form-control  "
              // v
              name="searchTerm"
              onChange={onValueChange}
            />
          </div>
        </div>
        <div className=" container ">
          <table className="table mt-5 table-striped justify-content-center">
            <thead>
              <tr>
                <th>Name</th>
                <th>Age</th>
                <th>Email</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody className="text-left">
              {userData.map((user) => (
                <tr key={user._id}>
                  <td>{user.name}</td>
                  <td>{user.age}</td>
                  <td>{user.email}</td>
                  <td>
                    <Link
                      to={{
                        pathname: `/updateuser/${user._id}`,
                      }}
                    >
                      <button
                        onClick={<Redirect to="/updateuser/${user._id}" />}
                        className="btn btn-success btn-sm m-1  "
                        // component={Link}
                        // to={`/updateuser/${user._id}`}
                      >
                        UPDATE
                      </button>
                    </Link>
                    <button
                      onClick={() => {
                        deleteUserData(user._id, user.name);
                      }}
                      className="btn btn-danger btn-sm m-1 "
                    >
                      DELETE
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <ReactPaginate
            previousLabel={"prev"}
            nextLabel={"next"}
            breakLabel={"..."}
            breakClassName={"break-me"}
            pageCount={pageCount}
            marginPagesDisplayed={2}
            pageRangeDisplayed={5}
            onPageChange={handlePageClick}
            containerClassName={"pagination"}
            subContainerClassName={"pages pagination"}
            activeClassName={"active"}
          />
        </div>
      </Router>
    </div>
  );
};
const mapStateToProps = (state) => {
  console.log("state===>", state.users, state.loading);
  const { users, loading } = state;
  // localStorage.setItem("useData", JSON.stringify(users));
  return {
    userData: users,
    loadingData: loading,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    fetchAllUsers: () => dispatch(fetchAllUsers()),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(UserContainerHook);
