import React, { Component, useEffect } from "react";
import { connect } from "react-redux";
import { fetchUsers } from "../redux";
import UserContainerForm from "./UserContainerForm";
function UserContainer({ userData, fetchUsers }) {
  useEffect(() => {
    fetchUsers();
  }, []);
  const getForm = (e) => {
    return (
      <div>
        <form onSubmit={this.submitHandler}>
          <div>
            <label>Name:</label>
            <input type="text" name="name" onChange={this.changeHandler} />
          </div>
          <div>
            <label>Age:</label>
            <input type="text" name="age" onChange={this.changeHandler} />
          </div>
          <button type="submit">Submit</button>
        </form>
      </div>
    );
  };

  return (
    <div>
      <h2>Users List</h2>
      <div className=" container ">
        <table className="table  table-striped justify-content-center">
          <thead>
            <tr>
              <th>Name</th>
              <th>Age</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody className="text-left">
            {userData.map((user) => (
              <tr key={user._id}>
                <td>{user.name}</td>
                <td>{user.age}</td>
                <td>
                  <button
                    onclick={this.getForm()}
                    className="btn btn-primary btn-sm m-1 "
                  >
                    ADD
                  </button>

                  <button className="btn btn-success btn-sm m-1  ">
                    UPDATE
                  </button>
                  <button className="btn btn-danger btn-sm m-1 ">DELETE</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
