import React, { Component } from "react";
import { connect } from "react-redux";
import { updateUsers } from "../redux";
// import React from 'react'
import { useLocation } from "react-router-dom";
// const location = useLocation();

function UserContainerUpdate({ userData, fetchUsers }, props) {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [user, setUser] = useState({ name: "", age: "" });
  const { name, age } = user;
  useEffect(() => {
    fetchUsers();
  }, []);
  const history = useHistory();

  return (
    <div>
      <Router>
        <h2>Users List</h2>
        <div className="container text-right">
          <Link to={{ pathname: `/adduser` }}>
            <button
              // onClick={<UserContainerForm />}
              className="btn btn-primary btn-md m-1  "
            >
              ADD USER
            </button>
          </Link>
        </div>
        <div className=" container ">
          <table className="table  table-striped justify-content-center">
            <thead>
              <tr>
                <th>Name</th>
                <th>Age</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody className="text-left">
              {userData.map((user) => (
                <tr key={user._id}>
                  <td>{user.name}</td>
                  <td>{user.age}</td>
                  <td>
                    <Link
                      to={{
                        pathname: `/updateuser/${user._id}`,
                      }}
                    >
                      <button
                        onClick={() => setModalIsOpen(true)}
                        className="btn btn-success btn-sm m-1  "
                        // component={Link}
                        // to={`/updateuser/${user._id}`}
                      >
                        UPDATE
                      </button>
                      <Modal isOpen={modalIsOpen}>
                        <div className="container ">
                          <form
                            onSubmit={(e) => {
                              e.preventDefault();
                              props.updateUsers(user, user.name, user.age);
                              console.log(
                                "update state",
                                user,
                                user.name,
                                user.age
                              );
                            }}
                          >
                            <div className="form-group">
                              <label>Name:</label>
                              <input
                                type="text"
                                name="name"
                                class="form-control"
                                defaultValue={user.name}
                                value={name}
                                onChange={(e) =>
                                  setUser({
                                    name: e.target.value,
                                  })
                                }
                              />
                            </div>
                            <div className="form-group">
                              <label for="text">Age:</label>
                              <input
                                type="text"
                                name="age"
                                class="form-control"
                                defaultValue={user.age}
                                value={age}
                                onChange={(e) =>
                                  setUser({
                                    age: e.target.value,
                                  })
                                }
                              />
                            </div>
                            <button
                              className="btn btn-primary"
                              onClick={() => setModalIsOpen(false)}
                              type="submit"
                            >
                              Update
                            </button>
                          </form>
                        </div>
                      </Modal>
                    </Link>
                    <button className="btn btn-danger btn-sm m-1 ">
                      DELETE
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </Router>
    </div>
  );
}
