import React, { useEffect, useState, useRef } from "react";
import { connect } from "react-redux";
import { deleteUsers, fetchSearchedUser, fetchAllUsers } from "../redux";
import { BrowserRouter as Router, Link, useHistory } from "react-router-dom";
import { useStateCallback } from "use-state-callback";
import ReactPaginate from "react-paginate";
const UserContainerHook = (
  { fetchAllUsers, userData, loadingData, totalPage, fetchSearchedUser },
  props
) => {
  const [users, setUsers] = useState(userData);
  const [offset, setOffset] = useStateCallback(1);
  const [searchTerm, setSearchTerm] = useState("");
  const [perPage] = useState(5);
  const [pageCount, setPageCount] = useState(0);
  const [selectPage, setSelectPage] = useState(0);
  const history = useHistory();
  const ref = useRef(userData);
  console.log(
    "%c 🤶: ref ",
    "font-size:16px;background-color:#586e1c;color:white;",
    ref
  );
  useEffect(() => {
    console.log("props", props);
    loadPage();
  }, []);
  useEffect(() => {
    setUsers([...users, userData]);
  }, [userData]);
  useEffect(() => {}, [offset]);
  const usePrevious = (value) => {
    const ref = useRef();

    useEffect(() => {
      ref.current = value;
    });
    return ref.current;
  };

  const prevOffset = usePrevious(offset);
  const prevUserData = usePrevious(userData);
  const prevTotalPage = usePrevious(totalPage);

  const loadPage = () => {
    console.log(
      "%c 🇲🇪: loadPage -> offset ",
      "font-size:16px;background-color:#f007ae;color:white;",
      offset
    );
    fetchAllUsers(offset, perPage);

    if (prevUserData !== userData || prevTotalPage !== totalPage) {
      console.log(
        "%c 🇱🇰: loadPage -> userData ",
        "font-size:16px;background-color:#c88547;color:white;",
        userData,
        prevTotalPage,
        totalPage
      );
      setUsers([...users, userData]);
      setPageCount(totalPage);
      // loadPage();
    }
    // setUsers(userData);
    // setPageCount(totalPage);
    console.log(
      "%c 💰: loadPage -> totalPage ",
      "font-size:16px;background-color:#c4523b;color:white;",
      prevTotalPage,
      totalPage
    );
  };

  const searchPage = (searchTerm, selectedPage) => {
    console.log(
      "%c ⚡: searchPage -> searchTerm ",
      "font-size:16px;background-color:#95b552;color:white;",
      searchTerm
    );
    fetchSearchedUser(searchTerm, selectedPage);

    setUsers(userData);
    setPageCount(totalPage);
  };

  const deleteUserData = async (id, name) => {
    if (window.confirm(`Are you sure you want to Delete ${name}?`)) {
      await deleteUsers(id);
    } else {
      history.push("/home");
    }
    fetchAllUsers(offset, perPage);
  };
  // const setOffsetData()
  const handlePageClick = (e) => {
    const selectedPage = e.selected;
    console.log(
      "%c 🏒: handlePageClick -> selectedPage ",
      "font-size:16px;background-color:#f17194;color:white;",
      selectedPage
    );
    setSelectPage(e.selected);
    if (searchTerm === "") {
      const page = selectedPage + 1;
      console.log(
        "%c 🇵🇾: handlePageClick -> page ",
        "font-size:16px;background-color:#9b8db4;color:white;",
        page
      );
      fetchAllUsers(page, perPage);
      console.log("userDAta @@", userData);
    } else {
      const page = selectedPage + 1;
      searchPage(searchTerm, page);
      // fetchSearchedUser(searchTerm, selectedPage);
      // setOffset(selectedPage + 2, () => {
      //   searchPage(searchTerm, offset);
      // });
    }
  };
  const addUser = async () => {
    history.push("/adduser");
    // <UserContainerHookForm />;
  };
  const editUserData = async (id) => {
    history.push(`/updateuser/${id}`);
    // this.refreshPage();
  };
  const onValueChange = (e) => {
    const searchTerm = e.currentTarget.value;
    setSearchTerm(searchTerm);
    if (searchTerm === "") {
      loadPage();
    } else {
      searchPage(searchTerm, offset);
    }
    // });
  };

  return (
    <div>
      <Router>
        <h2>Users List</h2>
        <div className="container text-right">
          <Link to={{ pathname: `/adduser` }}>
            <button onClick={addUser} className="btn btn-primary btn-md m-1  ">
              ADD USER
            </button>
          </Link>
        </div>
        <div className="container mt-mb-10 text-left">
          <div className="w-100 mt-mb-10  justify-content-left ui icon input">
            {/* <SearchPage /> */}
            <input
              // ref={inputEl}
              type="search "
              placeholder="Search Users"
              className="mt-mb-7 form-control  "
              // v
              name="searchTerm"
              onChange={onValueChange}
            />
          </div>
        </div>
        <div className=" container ">
          <table className="table mt-5 table-striped justify-content-center">
            <thead>
              <tr>
                <th>Name</th>
                <th>Age</th>
                <th>Email</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody className="text-left">
              {userData.map((user) => (
                <tr key={user._id}>
                  <td>{user.name}</td>
                  <td>{user.age}</td>
                  <td>{user.email}</td>
                  <td>
                    <Link
                      to={{
                        pathname: `/updateuser/${user._id}`,
                      }}
                    >
                      <button
                        onClick={() => editUserData(user._id)}
                        className="btn btn-success btn-sm m-1  "
                        // component={Link}
                        // to={`/updateuser/${user._id}`}
                      >
                        UPDATE
                      </button>
                    </Link>
                    <button
                      onClick={() => {
                        deleteUserData(user._id, user.name);
                      }}
                      className="btn btn-danger btn-sm m-1 "
                    >
                      DELETE
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <ReactPaginate
            previousLabel={"prev"}
            nextLabel={"next"}
            breakLabel={"..."}
            breakClassName={"break-me"}
            pageCount={pageCount}
            marginPagesDisplayed={2}
            pageRangeDisplayed={5}
            onPageChange={handlePageClick}
            containerClassName={"pagination"}
            subContainerClassName={"pages pagination"}
            activeClassName={"active"}
          />
        </div>
      </Router>
    </div>
  );
};
const mapStateToProps = (state) => {
  console.log("state===>", state);
  const { users, loading, page } = state;
  // localStorage.setItem("useData", JSON.stringify(users));
  return {
    userData: users,
    loadingData: loading,
    totalPage: page,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    fetchAllUsers: (offset, perPage) =>
      dispatch(fetchAllUsers(offset, perPage)),
    fetchSearchedUser: (searchTerm, offset) =>
      dispatch(fetchSearchedUser(searchTerm, offset)),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(UserContainerHook);
