import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { useHistory, Link } from "react-router-dom";
import { fetchUsers, insertUsers, fetchAllUsers } from "../redux";
import HeaderUser from "./HeaderUser";
import { useForm } from "react-hook-form";
import { useStateCallback } from "use-state-callback";

// import UserContainer from "./UserContainer";
const initialState = {
  name: "",
  age: "",
  email: "",
};
function UserContainerInsertHook({
  userData,
  fetchUsers,
  insertUsers,
  fetchAllUsers,
}) {
  const [user, setUser] = useStateCallback(initialState);
  const [errors, setErrors] = useState({});
  const { name, age, email } = user;
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { handleChange, handleSubmit } = useForm(validate);

  // const { id } = useParams();
  let history = useHistory();
  useEffect(() => {
    fetchUsers();
  }, []);
  useEffect(() => {
    if (Object.keys(errors).length === 0 && isSubmitting) {
    }
  }, [errors]);
  const onValueChange = (e) => {
    if (e.target.name === "email") {
      console.log("in if @@@@@@@");
      var re = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
      const r = re.test(e.target.value);
      if (r === false) {
      }
      console.log(
        "%c ðŸ‡¦ðŸ‡«: onValueChange -> r ",
        "font-size:16px;background-color:#dd77c2;color:white;",
        r,
        r === false
      );
    }
    e.preventDefault();
    setUser({ ...user, [e.target.name]: e.target.value });
  };
  function validate(values) {
    let errors = {};
    if (!values.email) {
      errors.email = "Email address is required";
    } else if (!/\S+@\S+\.\S+/.test(values.email)) {
      errors.email = "Email address is invalid";
    }
    if (!values.password) {
      errors.password = "Password is required";
    } else if (values.password.length < 8) {
      errors.password = "Password must be 8 or more characters";
    }
    return errors;
  }
  const addUser = async (e) => {
    if (!name || !age || !email) {
      alert("Please add all the details");
      history.push("/adduser");
      return;
    } else {
      fetchUsers();
      const existEmail = userData.filter((existingEmail) => {
        if (email === existingEmail.email) {
          return true;
        }
      });
      const map = existEmail.map((item) => {
        if (item.email === email) {
          return true;
        } else {
          return false;
        }
      });
      if (map.includes(true)) {
        setIsSubmitting(false);
        e.preventDefault();
        alert("Email already Exists");
        history.push("/adduser");
        return;
      } else {
        var re = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
        const r = re.test(user.email);
        if (r === false) {
          alert("Email is not valid");
        }
        setErrors(validate(user.email));
        console.log(
          "%c ðŸ‡ºðŸ‡¸: addUser -> Errors ",
          "font-size:16px;background-color:#a07fb7;color:white;",
          errors
        );
        setIsSubmitting(true);
        insertUsers(user);

        // history.push("/home");

        await fetchAllUsers();
      }
      //
    }
  };

  return (
    <div className="container ">
      <HeaderUser />
      <form>
        <div className="form-group text-left">
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={name}
            className="form-control"
            ref={handleChange}
            onChange={onValueChange}
          />
        </div>
        <div className="form-group text-left ">
          <label className="font-weight-bolder">Age:</label>
          <input
            type="text"
            name="age"
            value={age}
            className="form-control"
            // ref={handleChange}
            onChange={onValueChange}
          />
        </div>
        <div className="form-group text-left ">
          <label className="font-weight-bolder">Email:</label>
          <input
            type="email"
            name="email"
            value={email}
            className={` form-control input ${errors.email && "is-danger"}`}
            // ref={handleChange}
            onChange={onValueChange}
          />
          {errors.email && <p className="help is-danger">{errors.email}</p>}
        </div>
        <div className="form-group text-left">
          <Link to={{ pathname: `/home` }}>
            <button
              className="btn btn-primary"
              type="submit"
              onClick={(event) => handleSubmit(addUser(event))}
            >
              Submit
            </button>
          </Link>
        </div>
      </form>
    </div>
  );
}
const mapStateToProps = (state) => {
  console.log(
    "%c ðŸ‡¸ðŸ‡¿: mapStateToProps -> state ",
    "font-size:16px;background-color:#8c6d87;color:white;",
    state
  );
  return {
    userData: state.users,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    fetchUsers: () => dispatch(fetchUsers()),
    fetchAllUsers: () => dispatch(fetchAllUsers(1, 5)),
    insertUsers: (user) => dispatch(insertUsers(user)),
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(UserContainerInsertHook);
