import React, { Component, useEffect, useState } from "react";
import { connect } from "react-redux";
import { fetchUsers, updateUsers } from "../redux";
import UserContainerForm from "./UserContainerForm";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams,
  useHistory,
} from "react-router-dom";
import Modal from "react-modal";
// import UserContainerUpdate from "./UserContainerUpdate";
import childComponent from "./UserContainerUpdate";
import UserContainerUpdate from "./UserContainerUpdate";
// import { Modal } from "re";
// const history = useHistory();
function UserContainer({ userData, fetchUsers }) {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [state, setState] = useState({ name: "", age: "" });
  useEffect(() => {
    fetchUsers();
  }, []);
  const history = useHistory();

  return (
    <div>
      <Router>
        <h2>Users List</h2>
        <div className="container text-right">
          <Link to={{ pathname: `/adduser` }}>
            <button
              onClick={<UserContainerForm />}
              className="btn btn-primary btn-md m-1  "
            >
              ADD USER
            </button>
          </Link>
        </div>
        <div className=" container ">
          <table className="table  table-striped justify-content-center">
            <thead>
              <tr>
                <th>Name</th>
                <th>Age</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody className="text-left">
              {userData.map((user) => (
                <tr key={user._id}>
                  <td>{user.name}</td>
                  <td>{user.age}</td>
                  <td>
                    <Link
                      to={{
                        pathname: `/home`,
                      }}
                    >
                      <button
                        onClick={() => setModalIsOpen(true)}
                        className="btn btn-success btn-sm m-1  "
                      >
                        UPDATE
                      </button>
                      <Modal isOpen={modalIsOpen}>
                        <div className="container ">
                          <form
                          // onSubmit={(e) => {
                          //   e.preventDefault();
                          //   this.props.updateUsers(this.state);
                          // }}
                          >
                            <div className="form-group">
                              <label>Name:</label>
                              <input
                                type="text"
                                name="name"
                                class="form-control"
                                defaultValue={user.name}
                                value={state.name}
                                onChange={(e) =>
                                  setState({
                                    name: e.target.value,
                                  })
                                }
                              />
                            </div>
                            <div className="form-group">
                              <label for="text">Age:</label>
                              <input
                                type="text"
                                name="age"
                                class="form-control"
                                defaultValue={user.age}
                                value={state.age}
                                onChange={(e) =>
                                  setState({
                                    age: e.target.value,
                                  })
                                }
                              />
                            </div>
                            <button
                              className="btn btn-primary"
                              onClick={() => setModalIsOpen(false)}
                              type="submit"
                            >
                              Update
                            </button>
                          </form>
                        </div>
                      </Modal>
                    </Link>
                    <button className="btn btn-danger btn-sm m-1 ">
                      DELETE
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </Router>
    </div>
  );
}

const mapStateToProps = (state) => {
  console.log("state", state.users);
  return {
    userData: state.users,
  };
};
// const mapDispatchToProps = (dispatch) => {
//   console.log();
//   return {
//     fetchUsers: () => dispatch(fetchUsers()),
//     updateUsers:()=>dispatch(updateUsers())
//   };
// };
export default connect(mapStateToProps, { fetchUsers })(UserContainer);
