import React, { Component } from 'react';
import { connect } from 'react-redux'
import { fetchUsers } from '../redux'
function UserContainer({ userData, fetchUsers1 }) {
    useEffect(() => {
        fetchUsers1();
    }, []);
    return userData.loading ? (
        <h2>Loading</h2>
    ) : userData.error ? (
        <h2>{userData.error}</h2>
    ) : (
        <div>
            <h2>Users List</h2>
            <div>
                {userData &&
                    userData.users &&
                    userData.users.map((user) => <p key={user._id}>{user.name}</p>)}
                <button className="btn btn-primary btn-sm m-2">ADD</button>
                <button className="btn btn-success btn-sm m-2">UPDATE</button>
                <button className="btn btn-danger btn-sm m-2">DELETE</button>
            </div>
        </div>
    );
}
