  //   setUser(userData);
  // };

  // const history = useHistory();
  console.log("params DAta", useParams(), userData.name, userData.age);
  const onValueChange = (e) => {
    console.log(e.target.value);
    setUser({ ...usera, [e.target.name]: e.target.value });
  };
  const onSubmitData = (e) => {
    // e.preventDefault();
    updateUsers(id, name, age);
    console.log("update state", id, name, age);
  };
  console.log("update state", id, name, age);

  console.log("user======>", usera);
  return (
    <div className="container ">
      {userData.map((user) => {
        console.log("map user ==========>", user);
        return (
          <p key={user._id}>
            <form onSubmit={onSubmitData}>
              <div className="form-group text-left">
                <label>Name:</label>
                <input
                  type="text"
                  name="name"
                  className="form-control"
                  defaultValue={user.name}
                  value={usera}
                  onChange={setUser}
                />
              </div>
              <div className="form-group text-left">
                <label>Age:</label>
                <input
                  type="text"
                  name="age"
                  className="form-control"
                  value={age}
                  onChange={
                    ((e) => {
                      console.log(e.target.value);
                      setUser({ age: e.target.value });
                    }) && <Redirect to="/home" />
                  }
                />
              </div>
              <button
                className="btn btn-primary"
                // onClick={() => setModalIsOpen(false)}
                type="submit"
              >
                Update
              </button>
            </form>
          </p>
        );
      })}
    </div>
  );
}
