  useEffect(() => {
    fetchUsers();
  }, []);
  return (
    <div>
      <h2>Users List</h2>
      <div classname="font-weight-bolder">
        {userData.map((user) => (
          <p key={user._id}>
            {user.name}
            <button className="btn btn-primary btn-sm m-2">ADD</button>
            <button className="btn btn-success btn-sm m-2">UPDATE</button>
            <button className="btn btn-danger btn-sm m-2">DELETE</button>
          </p>
        ))}
      </div>
    </div>
  );
