import React, { Component, useEffect, useState, useRef } from "react";
import { connect } from "react-redux";
import { deleteUsers, fetchUsers, updateUsers } from "../redux";
import UserContainerForm from "./UserContainerForm";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams,
  useHistory,
  Redirect,
} from "react-router-dom";
import Modal from "react-modal";
import TextField from "@material-ui/core/TextField";
import Autocomplete from "@material-ui/lab/Autocomplete";
// import UserContainerUpdate from "./UserContainerUpdate";
import childComponent from "./UserContainerUpdate";
import UserContainerUpdate from "./UserContainerUpdate";
import SearchField from "react-search-field";
import SearchBar from "./SearchBar";
import SearchPage from "./searchPage";
import axios from "axios";

function UserContainer({ userData, fetchUsers }, props) {
  const [users, setUsers] = useState([]);
  const [user, setUser] = useState([]);
  const inputEl = useRef("");
  const { name, age, email } = user;
  useEffect(() => {
    fetchUsers();
    setUsers(userData);
  }, []);
  const history = useHistory();
  const deleteUserData = async (id, name) => {
    if (window.confirm(`Are you sure you want to Delete ${name}?`)) {
      await deleteUsers(id);
    } else {
      history.push("/home");
    }

    // await deleteUsers(id);
    fetchUsers();
  };
  const onSubmitDelete = (id) => {
    deleteUsers(id);
  };
  const addUser = async () => {
    history.push("/adduser");
    // <UserContainerForm />;
  };

  const filterContent = (users, searchTerm) => {
    const result = users.filter((user) => (
      <p key={user._id}>{user.name.includes(searchTerm.toLowerCase())}</p>
    ));
    console.log("result", result);
    setUsers(result);
  };
  const onValueChange = (e) => {
    console.log(e.currentTarget.value);
    const searchTerm = e.currentTarget.value;
    axios.get("http://localhost:9000/").then((response) => {
      if (response.data) {
        filterContent(response.data, searchTerm);
        // const users = response.data;
        // console.log("Users for search", users);
        // // setUser(users);
        // console.log("users search", user, setUser);
      }
    });
    // setUser({ ...user, [e.target.name]: e.target.value });
  };

  return (
    <div>
      <Router>
        <h2>Users List</h2>

        <div className="container text-right">
          <Link to={{ pathname: `/adduser` }}>
            <button onClick={addUser} className="btn btn-primary btn-md m-1  ">
              ADD USER
            </button>
          </Link>
        </div>
        <div className="container text-left">
          <div className="w-75 mb-10  justify-content-left ui icon input">
            {/* <SearchPage /> */}
            <input
              // ref={inputEl}
              type="search "
              placeholder="Search Users"
              className="mb-7 form-control  "
              // v
              name="searchTerm"
              onChange={onValueChange}
            />
            <i className="search icon"></i>
          </div>
        </div>
        <div className=" container ">
          <table className="table  table-striped justify-content-center">
            <thead>
              <tr>
                <th>Name</th>
                <th>Age</th>
                <th>Email</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody className="text-left">
              {users.map((user) => (
                <tr key={user._id}>
                  <td>{user.name}</td>
                  <td>{user.age}</td>
                  <td>{user.email}</td>
                  <td>
                    <Link
                      to={{
                        pathname: `/updateuser/${user._id}`,
                      }}
                    >
                      <button
                        onClick={<Redirect to="/updateuser/${user._id}" />}
                        className="btn btn-success btn-sm m-1  "
                        // component={Link}
                        // to={`/updateuser/${user._id}`}
                      >
                        UPDATE
                      </button>
                    </Link>

                    <button
                      onClick={() => {
                        deleteUserData(user._id, user.name);
                      }}
                      className="btn btn-danger btn-sm m-1 "
                    >
                      DELETE
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </Router>
    </div>
  );
}

const mapStateToProps = (state) => {
  console.log("state", state.users);
  return {
    userData: state.users,
  };
};
// const mapDispatchToProps = (dispatch) => {
//   console.log();
//   return {
//     fetchUsers: () => dispatch(fetchUsers()),
//     updateUsers:()=>dispatch(updateUsers())
//   };
// };
export default connect(mapStateToProps, { fetchUsers, updateUsers })(
  UserContainer
);
