import React, { Component, useState, useEffect } from "react";
import { connect } from "react-redux";
import { fetchUser, updateUsers } from "../redux";
// import React from 'react'
import { useLocation, useParams } from "react-router-dom";
// const location = useLocation();

function UserContainerUpdate({ userData, fetchUser }, props) {
  const [user, setUser] = useState({ name: "", age: "" });
  const { name, age } = user;
  const { id } = useParams();
  console.log("params DAta", useParams(), userData);
  useEffect(() => {
    fetchUser(id);
  }, []);
  // const history = useHistory();

  return (
    <div className="container ">
      <h4>{userData}</h4>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          props.updateUsers(user, user.name, user.age);
          console.log("update state", user, user.name, user.age);
        }}
      >
        <div className="form-group text-left">
          <label className="text-left">Name:</label>
          <input
            type="text"
            name="name"
            className="form-control"
            defaultValue={user.name}
            value={name}
            onChange={(e) =>
              setUser({
                name: e.target.value,
              })
            }
          />
        </div>
        <div className="form-group text-left">
          <label for="text">Age:</label>
          <input
            type="text"
            name="age"
            className="form-control"
            defaultValue={user.age}
            value={age}
            onChange={(e) =>
              setUser({
                age: e.target.value,
              })
            }
          />
        </div>
        <button
          className="btn btn-primary"
          // onClick={() => setModalIsOpen(false)}
          type="submit"
        >
          Update
        </button>
      </form>
    </div>
  );
}
