import React, { Component } from "react";
import { connect } from "react-redux";
import { insertUsers } from "../redux";
class UserContainerForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      age: "",
    };
  }
  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  submitHandler = (e) => {
    e.preventDefault();
    console.log(this.state);
    this.props.insertUsers(this.state);
  };
  render() {
    const { name, age } = this.state;
    return (
      <div>
        <form action="/UserContainer.js" onSubmit={this.submitHandler}>
          <div>
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={name}
              onChange={this.changeHandler}
            />
          </div>
          <div>
            <label>Age:</label>
            <input
              type="text"
              name="age"
              value={age}
              onChange={this.changeHandler}
            />
          </div>
          <button type="submit">Submit</button>
        </form>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    userData: state.user,
  };
};
export default connect(mapStateToProps, { insertUsers })(UserContainerForm);
