import React, { Component } from "react";
import { connect } from "react-redux";
import { updateUsers } from "../redux";
class UserContainerPut extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      age: "",
    };
  }
  getUser = (name, age) => {
    this.setState({
      name: name,
      age: age,
    });
  };
  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  submitHandler = (e) => {
    e.preventDefault();
    console.log(this.state);
    this.props.updateUsers(this.state);
  };
  render() {
    const { name, id } = this.state;
    return (
      <div>
        <form onSubmit={this.submitHandler}>
          <div>
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={name}
              onChange={this.changeHandler}
            />
          </div>
          <div>
            <label>Id:</label>
            <input
              type="text"
              name="id"
              value={id}
              onChange={this.changeHandler}
            />
          </div>
          <button type="submit">Update</button>
        </form>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  console.log("state", state);
  return {
    userData: state.user,
  };
};
export default connect(
  mapStateToProps,
  // mapDispatchToProps{}
  { updateUsers }
)(UserContainerPut);
