  useLocation,
  useParams,
  Redirect,
  useHistory,
  Link,
} from "react-router-dom";
import { deleteUsers, fetchUsers, insertUsers } from "../redux";
import HeaderUser from "./HeaderUser";
import UserContainer from "./UserContainer";
const initialState = {
  name: "",
  age: "",
  email: "",
};
